import React, { useState } from 'react';
import PaymentComponent from './PaymentComponent';

const MyComponent = ({ data }) => {
  const [purchased, setPurchased] = useState(false);

  const handlePurchase = () => {
    // Perform purchase actions here
    setPurchased(true);


  };

  return (
    <div className="row">
      {data.map((data, index) => (
        <div className="col-md-4" key={index}>
          <div className="card" style={{ width: '18rem' }}>
            <img className="card-img-top" src={data.recipe.image} alt="Card cap" />
            <div className="card-body">
              <center>
                <h5 className="card-title">{data.recipe.label}</h5>
                <p className="card-text">Total Amount Of Calories: {Math.round(data.recipe.calories)}</p>
                <button className="btn btn-primary" onClick={handlePurchase}>Buy</button>
              </center>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MyComponent;
