import React, { useState } from 'react';
import PaymentComponent from './PaymentComponent';

const MyComponent = ({ data }) => {
  // Initialize an array to track purchased state for each item
  const [purchasedItems, setPurchasedItems] = useState([]);
  // State to control the visibility of the PaymentComponent
  const [showPayment, setShowPayment] = useState(false);

  const handleBuyClick = (index) => {
    // Mark the specific item as purchased and show the payment component
    setPurchasedItems((prevItems) => [...prevItems, index]);
    setShowPayment(true);
  };

  const handleCloseClick = () => {
    // Hide the payment component
    setShowPayment(false);
  };

  return (
    <div className="row">
      {data.map((dataItem, index) => (
        <div className="col-md-4" key={index}>
          <div className="card" style={{ width: '18rem' }}>
            <img className="card-img-top" src={dataItem.recipe.image} alt="Card cap" />
            <div className="card-body">
              <center>
                <h5 className="card-title">{dataItem.recipe.label}</h5>
                <p className="card-text">Total Amount Of Calories: {Math.round(dataItem.recipe.calories)}</p>
                {purchasedItems.includes(index) && showPayment ? (
                  <>
                    <PaymentComponent />
                    <button className="btn btn-secondary" onClick={handleCloseClick}>Close</button>
                  </>
                ) : (
                  <button className="btn btn-primary" onClick={() => handleBuyClick(index)}>Buy</button>
                )}
              </center>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MyComponent;
