import React, { useState } from 'react';

const PaymentComponent = ({ onClose }) => {
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvc, setCVC] = useState('');
  const [otp, setOtp] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState('');

  const handlePayment = () => {
    // Simulate sending card details to the server and requesting OTP
    console.log('Payment details submitted:', cardNumber, expiryDate, cvc);
    setIsOtpSent(true);
  };

  const handleOtpSubmit = () => {
    // Simulate verifying OTP with the server
    console.log('OTP submitted:', otp);
    // If OTP is correct, set payment status to success
    setPaymentStatus('Successful payment done');
    // Close the payment component after successful payment
    onClose();
  };

  return (
    <div>
      <h2>Choose Payment Option</h2>
      {!isOtpSent ? (
        <>
          <div>
            <label>Card Number:</label>
            <input type="text" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} />
          </div>
          <div>
            <label>Expiry Date:</label>
            <input type="text" value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)} />
          </div>
          <div>
            <label>CVC:</label>
            <input type="text" value={cvc} onChange={(e) => setCVC(e.target.value)} />
          </div>
          <button onClick={handlePayment}>Proceed to Payment</button>
        </>
      ) : (
        <>
          <div>
            <label>Enter OTP:</label>
            <input type="text" value={otp} onChange={(e) => setOtp(e.target.value)} />
          </div>
          <button onClick={handleOtpSubmit}>Submit</button>
        </>
      )}
      {paymentStatus && <p>{paymentStatus}</p>}
    </div>
  );
};

export default PaymentComponent;
